pub fn install() {
    Agent::new("ridley")
    .set_costume([0, 1, 2, 3, 4, 5, 6, 7].to_vec())
    .game_acmd("game_attacks3", ssbexo_ridley_forward_tilt_acmd, Low)
    .game_acmd("game_attacks3hi", ssbexo_ridley_forward_tilt_hi_acmd, Low)
    .game_acmd("game_attacks3lw", ssbexo_ridley_forward_tilt_lw_acmd, Low)
    .install()
    ;
}