pub fn install() {
    Agent::new("shulk")
    .set_costume([0, 1, 2, 3, 4, 5, 6, 7].to_vec())
    .game_acmd("game_attacks4", ssbexo_shulk_forward_smash_acmd, Low)
    .game_acmd("game_attacks4hi", ssbexo_shulk_forward_smash_hi_acmd, Low)
    .game_acmd("game_attacks4lw", ssbexo_shulk_forward_smash_lw_acmd, Low)
    .game_acmd("game_attacklw4", ssbexo_shulk_down_smash_acmd, Low)
    .install()
    ;
}