pub fn install() {
    Agent::new("shulk")
    .set_costume([0, 1, 2, 3, 4, 5, 6, 7].to_vec())
    .game_acmd("game_specials", ssbexo_shulk_back_slash_acmd, Low)
    .game_acmd("game_specialairs", ssbexo_shulk_back_slash_acmd, Low)
    .game_acmd("game_specialairsfall", ssbexo_shulk_back_slash_fall_acmd, Low)
    .install()
    ;
}