pub fn install() {
    Agent::new("captain")
    .game_acmd("game_attacks3", ssbexo_captain_forward_tilt_acmd)
    .game_acmd("game_attacks3hi", ssbexo_captain_forward_tilt_hi_acmd)
    .game_acmd("game_attacks3lw", ssbexo_captain_forward_tilt_lw_acmd)
    .game_acmd("game_attackhi3", ssbexo_captain_up_tilt_acmd)
    .game_acmd("game_attacklw3", ssbexo_captain_down_tilt_acmd)
    .install()
    ;
}