pub fn install() {
    Agent::new("marth")
    .game_acmd("game_attack11", ssbexo_marth_jab_1_acmd)
    .game_acmd("game_attack12", ssbexo_marth_jab_2_acmd)
    .game_acmd("game_attackdash", ssbexo_marth_dash_attack_acmd)
    .install()
    ;
}