pub fn install() {
    Agent::new("marth")
    .game_acmd("game_attacks4", ssbexo_marth_forward_smash_acmd, Priority::Low)
    .game_acmd("game_attackhi4", ssbexo_marth_up_smash_acmd, Priority::Low)
    .game_acmd("game_attacklw4", ssbexo_marth_down_smash_acmd, Priority::Low)
    .install()
    ;
}