pub fn install() {
    Agent::new("peach")
    .game_acmd("game_attackairn", ssbexo_peach_nair_acmd, Priority::Low)
    .game_acmd("game_attackairf", ssbexo_peach_fair_acmd, Priority::Low)
    .install()
    ;
}