pub fn install() {
    Agent::new("trail")
    .game_acmd("game_attack11", ssbexo_trail_jab_1_acmd, Priority::Low)
    .game_acmd("game_attack12", ssbexo_trail_jab_2_acmd, Priority::Low)
    .game_acmd("game_attack13", ssbexo_trail_jab_3_acmd, Priority::Low)
    .game_acmd("game_attackdash", ssbexo_trail_dash_attack_acmd, Priority::Low)
    .install()
    ;
}