pub fn install() {
    Agent::new("wolf")
    .game_acmd("game_attacks4", ssbexo_wolf_forward_smash_acmd)
    .game_acmd("game_attackhi4", ssbexo_wolf_up_smash_acmd)
    .game_acmd("game_attacklw4", ssbexo_wolf_down_smash_acmd)
    .install()
    ;
}