pub fn install() {
    Agent::new("yoshi")
    .game_acmd("game_attack11", ssbexo_yoshi_jab_1_acmd, Priority::Low)
    .game_acmd("game_attackdash", ssbexo_yoshi_dash_attack_acmd, Priority::Low)
    .install()
    ;
}